int correcDateToWorkWith(int y, int m, int d)
{
	if (y < 2000 || m < 1 || m > 12 || d<1)
	{
		return 0;
	}
	if (y % 4 == 0 && (y % 100 != 0 || y % 400 == 0) && y % 4000 != 0)
		months[1] = 29;
    else
    {
        months[1] = 28;
    }
	if (months[m - 1] >= d)
	{
		return 1;
	}
	else {
		return 0;
	}
}

int IsWorkDay(int y, int m, int d)
{
	if (correcDateToWorkWith(y, m, d))
	{
		int newD = d;
		int newY = y;
		if (m < 3)
		{
			newD += newY;
			--newY;
		}
		else
		{
			newD += newY - 2;
		}
		int day = ((23 * m / 9 + newD + 4 + newY / 4 + newY / 400 - newY / 100 - newY / 4000) - 1) % 7;
		if (day < 5)
		{
			if ((d == 1 && m == 1) || (d == 1 && m == 5) || (d == 8 && m == 5) || (d == 5 && m == 7) || (d == 6 && m == 7) || (d == 28 && m == 9)
				|| (d == 28 && m == 10) || (d == 17 && m == 11) || (d == 24 && m == 12) || (d == 25 && m == 12) || (d == 26 && m == 12))
			{
				return 0;
			}
			else {
				return 1;
			}
		}

		else {
			return 0;
		}
	}
	else
	{
		return 0;
	}
}


int HowManyDays(int y1, int m1, int d1, int y2, int m2, int d2)
{
	if (y1 % 4 == 0 && (y1 % 100 != 0 || y1 % 400 == 0) && y1 % 4000 != 0)
		months[1] = 29;
    else{
        months[1] = 28;
    }
	int newD = d1;
	int newY = y1;
	int ans = 0;
	if (m1 < 3)
	{
		newD += newY;
		--newY;
	}
	else
	{
		newD += newY - 2;
	}
	int day = ((23 * m1 / 9 + newD + 4 + newY / 4 + newY / 400 - newY / 100 - newY / 4000) - 1) % 7;
        while (1)
		{
		    if(y1==y2)
              if (m1 == m2)
                 if (d1 == d2)
                   break;
	    if(day<5){
		if(holidays[m1][d1]==0)
        {
            ans+=1;
        }
        }
		d1 += 1;
		if (d1 > months[m1 - 1])
		{
			m1 += 1;
			d1 = 1;
			if (m1 == 13)
			{
				m1 = 1;
				y1 += 1;
				if (leapYears[y1%4000]==1)
				{
					months[1] = 29;
				}
				else
				{
					months[1] = 28;
				}
			}
		}
		day += 1;
		if (day == 7)
		{
			day = 0;
		}
	}
	if(day<5){
		if(holidays[m1][d1]==0)
        {
            ans+=1;
        }
    }

	return ans;
}

int HowManyDaysInYears(int y1, int y2)
{
            int i,newD,newY,day,ans=0;
            newD = 1;
            newY = y1+1;
            newD += newY;
            --newY;
            day = ((23 * 1 / 9 + newD + 4 + newY / 4 + newY / 400 - newY / 100 - newY / 4000) - 1) % 7;

			for (i = y1 + 1; i < y2; ++i)
			{
				if (leapYears[i%4000])
				{
					ans += workingDaysInYear[day] - numberOfPublicHolidays[day];
                    day+=2;
                    if(day >= 7)
                    day-=7;
				}
				else
				{
                    ans += workingDaysInYear[day+7] - numberOfPublicHolidays[day+7];
                    day+=1;
                    if(day >= 7)
                    day-=7;
				}
			}
			return ans;
}

int CountWorkDays(int y1, int m1, int d1, int y2, int m2, int d2, int * cnt)
{
	if (correcDateToWorkWith(y1, m1, d1) && correcDateToWorkWith(y2, m2, d2) && (y2>y1 || (y2 == y1 && m2 > m1) || (y2 == y1 && m2 == m1 && d2 >= d1)))
	{
		*cnt = 0;
		holidays[1][1]=1;
		holidays[5][1]=1;
		holidays[5][8]=1;
		holidays[7][5]=1;
		holidays[7][6]=1;
		holidays[9][28]=1;
		holidays[10][28]=1;
		holidays[11][17]=1;
		holidays[12][24]=1;
		holidays[12][25]=1;
		holidays[12][26]=1;
		if (y2 > y1 + 1)
		{

			*cnt = HowManyDays(y1, m1, d1, y1, 12, 31);

            if(y2>7999)
            {
                int counter = 3999,i=0;
                *cnt += HowManyDaysInYears(y1,4000);
                while(y2>=counter+4000 && counter+4000<10000000)
                {
                    counter+=4000;
                    *cnt += fourK[i];
                    i++;
                }
                *cnt += HowManyDaysInYears(counter,y2);
            }
            else{
                *cnt += HowManyDaysInYears(y1,y2);
            }

			*cnt += HowManyDays(y2, 1, 1, y2, m2, d2);
		}
		else {
			*cnt = HowManyDays(y1, m1, d1, y2, m2, d2);
		}
		return 1;
	}
	else
	{
		return 0;
	}
	return 0;
/*Pavel Jahoda*/
}

